    opacity: 0;
    animation: showText2 0.5s 1.5s linear forwards;
}

@keyframes showText2 {
    100% {
        opacity: 1;
    }
}

.progress-line.nodejs span::after {
    content: "90%";
}

.progress-line.css span::after {
    content: "60%";
}

.progress-line.mongo span::after {
